import React, { Component } from "react";

import Auxiliary from "../../hoc/Auxiliary";
import Burger from "../../components/Burger/Burger";

class BurgerBuilder extends Component {

    state = {
        ingredients: {
            salad: 1,
            meat: 1,
            cheese: 2,
            bacon: 0,
        }
    }
    render () {
        return (
            <Auxiliary>
                <Burger />
                <div>Build Controls</div>
            </Auxiliary>
        );
    }
}

export default BurgerBuilder
